/**
 * 防抖函数
 * @param {*} cb 回调函数，要执行的操作
 * @param {*} delay 延时时间
 */
const debounce = (cb, delay = 1000) => {
  let timeout;
  return (...args) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => {
      cb(...args, delay);
    });
  };
};

const throttle = (cb, delay = 1000) => {
  let isWaiting = false;
  let lastArgs;

  const timeOutFn = () => {
    if (lastArgs) {
      cb(...lastArgs);
      lastArgs = null;
      setTimeout((timeOutFn, delay));
    } else {
      isWaiting = false;
    }
  };

  return (...args) => {
    if (isWaiting) {
      lastArgs = args;
      return;
    }
    cb(...args);
    isWaiting = true;
    setTimeout((timeOutFn, delay));
  };
};

function deepClone(target,original) { 
  for(let k in original){
    if(typeof original[k] === "object"){
      target[k] = {}
      deepClone(target[k],original[k])
    }else{
      target[k] = original[k]
    }
  }
}

