import Vue from "vue";
import Vuex from "vuex";
Vue.use(Vuex);

// state：仓库存储数据的地方
const state = {
  count: 1,
};
// mutations：修改 state 的唯一手段
const mutations = {
  ADD(state, count) {
    state.count++;
  },
};
// action：处理 action，可以书写自己的业务逻辑，也可以处理异步
const actions = {
  // 此处禁止修改 state
  add({ commit }) {
    commit("ADD");
  },
};
// getters：理解为计算属性，用于简化仓库数据，让组件获取仓库的数据更加方便
const getters = {};

// 对外暴露 store 类的实例
export default new Vuex.Store({
  state,
  mutations,
  actions,
  getters,
});

