// 全局前置守卫
router.beforeEach((to, from, next) => {
    // to 是将要方位的路由的信息对象
    // from 是将要离开的路由的信息对象
    // next 是一个函数，调用 next() 表示放行，允许这次路由导航
})

router.beforeEach((to, from, next) => {
    if(to.path === '/main'){
        const token = localStorage.getItem('token')
        if(token){
            next() // 访问的是后台主页，且有 token 的值
        }else{
            next('/login') // 访问的是后台主页，但是没有 token 的值
        }
    } else {
        next() // 访问的不是后台主页，直接放行
    }
})
