    template: './src/index.html', // 指定源文件的存放路径
    filname: './index.html', // 指定生成的文件的存放路径
})

module.exports = {
    mode: 'development',
    plugins: [htmlPlugin], // 3. 通过 plugins 节点，使 htmlPlugin 插件生效
}

