// 引入 nprogress 进度条
import nprogress from "nprogress";
// 引入 nprogress 进度条样式
import "nprogress/nprogress.css";

// 请求拦截器
requests.interceptors.request.use((config) => {
  // ...
  // 进度条开始动
  nprogress.start();
  // ...
  return config;
});
// 响应拦截器
requests.interceptors.response.use(
  // 响应成功的回调函数
